tinymce.PluginManager.add('customcheckbox', (editor, url) => {
	
	const options = editor.getParam('customcheckbox') || {}
	const path = options.path || url


editor.ui.registry.addIcon('customcheckbox', `
	<svg xmlns="http://www.w3.org/2000/svg" width="120" height="24" viewBox="0 0 24 24">
	  <text x="0" y="18" font-size="16" font-family="Arial" fill="white">체크</text>
	</svg>`);


editor.ui.registry.addButton('customcheckbox', {
  icon: 'customcheckbox',
  tooltip: '체크박스',
  onAction: () => {
	editor.insertContent('<input type="checkbox">');
  },
  onPostRender: function() {
    let _this = this // reference to the button itself
    editor.on('NodeChange', function(e) {
      _this.active(
        e.element.className.indexOf('fm-editor-equation') > -1 &&
          e.element.nodeName.toLowerCase() == 'img'
      )
    })
  }
})
});